INSERT INTO `clients` (`id`, `title`, `filename`, `annotation`, `text`) VALUES
(1, 'Ингосстрах Страхование', '1_c4ca42.gif', 'Уникальный проект по разработке и автоматизации карьеры и кадрового резерва в рамках всей компании. На той же платформе были автоматизированы процессы управления эффективностью, обучением и развитием сотрудников', ''),
(2, 'МТС Украина', '2_c81e72.gif', 'Автоматизация подбора в МТС Украина (сроки внедрения – 4 месяца, период окупаемости – 1 год, возврат на инвестиции – 130%, экономия в год – 250 тыс. евро) и разработка политики управления карьерой различных групп персонала МТС Россия', ''),
(3, 'Видео Интернешнл', '3_eccbc8.gif', 'Проект по комплексной автоматизации HR процессов ГК и дочерних предприятий: оценка эффективности, оценка методом  360 градусов, управления карьерой и кадровым резервом. ', '<p>\n	Ингосстрах Страхование</p>\n'),
(5, 'Северсталь', '5_e4da3b.gif', 'Автоматизация процессов управления талантами многонациональной компании с развертыванием системы на зарубежные предприятия', ''),
(6, 'Сибур', '6_167909.gif', 'Внедрение автоматизированных процессов оценки и развития с использованием отраслевого каталога провайдеров обучения', ''),
(13, 'АФК Система', '13_c51ce4.gif', 'Развертывание комплексной системы оценки по КПЭ и компетенциям за 2 месяца, одновременная оценка 12 000 сотрудников', ''),
(7, 'Пежо Ситроен Митцубиси', '7_8f14e4.gif', 'Проект по автоматизации процесса подбора как "белых", так и "синих" воротничков. Создание карьерной страницы для привлечения кандидатов.', ''),
(8, 'ММВБ', '8_c9f0f8.gif', 'Поддержка внедрения изменений методологии постановки и оценки по целям через автоматизацию процесса на платформе Lumesse ETWeb', ''),
(9, 'Русал', '9_45c48c.gif', 'Автоматизция процессов управления эффективностью - планирования, постановки и каскадирования целей, оценки по КПЭ - на платформе ETWeb', ''),
(10, 'ТехноНИКОЛЬ', '10_d3d944.png', 'Пилотный проект автоматизации процесса оценки сотрудников по целям и оценки по компетенциям методами 180 и 360 градусов, подготовки планов развития по итогам.', ''),
(11, 'Сбербанк Лизинг', '11_6512bd.gif', 'Пилотный проект автоматизации процесса оценки эффективности топ менеджеров по целям, компетенциям и уровню выполнения плановых мероприятий.', ''),
(12, 'Kelly OCG', '12_c20ad4.gif', 'Создание единой автоматизированной площадки для предоставления услуг поиска и подбора персонала для главного спортивного события 2014 года в городе Сочи и другим клиентам', '');
